(323, 'paip2019', 'PAIP 2019', 'Address liver cancer segmentation and viable tumor burden estimation', 'PAIP2019: Liver Cancer Segmentation Task 1: Liver Cancer Segmentation Task 2: Viable Tumor Burden Estimation', 'https://rumc-gcorg-p-public.s3.amazonaws.com/logos/challenge/370/Untitled_design.png', 'https://paip2019.grand-challenge.org/', 'completed', 5, NULL, NULL, NULL, NULL, '2023-11-08 00:42:00', '2023-11-17 23:27:53'),
(324, 'orcascore', 'orCaScore', 'Coronary artery calcium scoring in cardiac CT scans', 'The purpose of the orCaScore challenge is to compare methods for automatic and semi-automatic coronary artery calcium scoring in cardiac CT scans. This evaluation framework was launched at the MICCAI 2014 workshops in Boston, USA, where we organized the Challenge on Automatic Coronary Calcium Scoring.', 'https://rumc-gcorg-p-public.s3.amazonaws.com/logos/challenge/378/00b670348bfdf4464e00c44310ec259f.jpg', 'https://orcascore.grand-challenge.org/', 'active', 5, 'https://doi.org/10.1118/1.4945696', NULL, NULL, NULL, '2023-11-08 00:42:00', '2023-11-17 23:28:02'),
(325, 'curious2019', 'curious2019', 'MRI to intra-operative ultrasound (iUS) before and after tumor resection', 'MICCAI Challenge 2019 for Correction of Brainshift with Intra-Operative Ultrasound. Taks 1: Register pre-operative MRI to iUS before tumor resection;Taks 2: Register iUS after tumor resection to iUS before tumor resection', 'https://rumc-gcorg-p-public.s3.amazonaws.com/logos/challenge/380/CuRIOUS.png', 'https://curious2019.grand-challenge.org/', 'completed', 5, NULL, NULL, NULL, NULL, '2023-11-08 00:42:00', '2023-11-17 23:28:13'),
(326, 'patchcamelyon', 'PatchCamelyon', 'Detect breast cancer metastasis in lymph nodes', 'PatchCamelyon is a new and challenging image classification dataset of 327.680 color images (96 x 96px) extracted from histopathology images of the CAMELYON16 challenge. The goal is to detect breast cancer metastasis in lymph nodes.', 'https://rumc-gcorg-p-public.s3.amazonaws.com/logos/challenge/381/Screen_Shot_2019-05-05_at_21.43.25.png', 'https://patchcamelyon.grand-challenge.org/', 'active', 5, NULL, NULL, NULL, NULL, '2023-11-08 00:42:00', '2023-11-17 23:28:22'),
(327, 'endovissub2019-scared', 'Stereo Correspondence and Reconstruction of Endoscopic Data', 'Address stereo correspondence and reconstruction challenges in endoscopic data', 'Stereo correspondence and reconstruction of endoscopic data', 'https://rumc-gcorg-p-public.s3.amazonaws.com/logos/challenge/385/c7714704.jpg', 'https://endovissub2019-scared.grand-challenge.org/', 'completed', 5, NULL, NULL, NULL, NULL, '2023-11-08 00:42:00', '2023-11-08 00:58:30'),
(328, 'verse2019', 'VerSe`19', 'Vertebrae labelling and segmentation on 150 CT scans', 'Vertebrae labelling and segmentation on a spine dataset on an unprecedented 150 CT scans with voxel-level vertebral annotations.', 'https://rumc-gcorg-p-public.s3.amazonaws.com/logos/challenge/388/logo_border.png', 'https://verse2019.grand-challenge.org/', 'active', 5, NULL, NULL, NULL, NULL, '2023-11-08 00:42:00', '2023-11-17 21:30:59'),
(329, 'gleason2019', 'Gleason2019', 'Methods for prostate cancer from H&E-stained histopathology images', 'MICCAI 2019 automatic prostate gleason grading challenge: this challenge aims at the automatic gleason grading of prostate cancer from h&e-stained histopathology images. This task is of critical importance because gleason score is a strong prognostic predictor. On the other hand, it is very challenging because of the large degree of heterogeneity in the cellular and glandular patterns associated with each gleason grade, leading to significant inter-observer variability, even among expert pathologists.', 'https://rumc-gcorg-p-public.s3.amazonaws.com/logos/challenge/391/GLEASON2019.png', 'https://gleason2019.grand-challenge.org/', 'completed', 5, NULL, NULL, NULL, NULL, '2023-11-08 00:42:00', '2023-11-14 20:00:08'),
(330, 'age', 'AGE', 'Assess automatic methods for angle closure glaucoma evaluation', 'Angle closure glaucoma evaluation challenge', 'https://rumc-gcorg-p-public.s3.amazonaws.com/logos/challenge/394/icon.png', 'https://age.grand-challenge.org/', 'active', 5, NULL, NULL, NULL, NULL, '2023-11-08 00:42:00', '2023-11-11 01:48:53'),
(331, 'amd', 'iChallenge-AMD', 'Tackle challenges in age-related macular degeneration diagnosis and analysis', 'Age-related macular degeneration challenge', 'https://rumc-gcorg-p-public.s3.amazonaws.com/logos/challenge/395/%E6%BC%94%E7%A4%BA%E6%96%87%E7%A8%BF1-2.png', 'https://amd.grand-challenge.org/', 'active', 5, NULL, NULL, NULL, NULL, '2023-11-08 00:42:00', '2023-11-08 00:58:34'),
(332, 'structseg2019', 'StructSeg2019', 'Automated structure segmentation for radiotherapy planning', 'Welcome to automatic structure segmentation for radiotherapy planning challenge 2019. This competition is part of the MICCAI 2019 challenge.', 'https://rumc-gcorg-p-public.s3.amazonaws.com/logos/challenge/398/logo_aAzg3xS.jpg', 'https://structseg2019.grand-challenge.org/', 'completed', 5, NULL, NULL, NULL, NULL, '2023-11-08 00:42:00', '2023-11-14 19:54:50'),
(333, 'digestpath2019', 'DigestPath2019', 'Algorithms for signet ring cell detection and colonoscopy tissue screening', 'The challenge aims to evaluate algorithms for signet ring cell detection and colonoscopy tissue screening in digestive system pathological images. It introduces the first public dataset for these tasks, providing expert-level annotations to advance research on automatic pathological object detection and lesion segmentation.', 'https://rumc-gcorg-p-public.s3.amazonaws.com/logos/challenge/399/digestpath-logo2.png', 'https://digestpath2019.grand-challenge.org/', 'completed', 5, NULL, NULL, NULL, NULL, '2023-11-08 00:42:00', '2023-11-15 18:40:29'),
